#!/usr/bin/env python3

# INPUT

def parser(text) -> str:
    return text.strip()


def read_input() -> str:
    with open(__file__, encoding="utf-8") as f:
        c = f.read()
        data = c[c.rindex("ðŸŽ…") + 1: c.rindex("â›„")].strip()
    return data


# MAIN
def part1(puzzle):
    counter = 0
    for char in puzzle:
        if char == ')':
            counter -= 1
        if char == '(':
            counter += 1
    return counter


def part2(puzzle: str) -> int:
    counter = 0
    for position, char in enumerate(puzzle):
        if char == ')':
            counter -= 1
        if char == '(':
            counter += 1
        if counter < 0:
            return position + 1
    return counter


# TEST
def test():
    # GIVEN
    assert part1('(())') == 0
    assert part1('()()') == 0
    assert part1('(((') == 3
    assert part1('(()(()(') == 3
    assert part1('))(((((') == 3
    assert part1('())') == -1
    assert part1('))(') == -1
    assert part1(')))') == -3
    assert part1(')())())') == -3
    # part 2
    assert part2(')') == 1
    assert part2('()())') == 5
    return True


if __name__ == '__main__':
    assert test()
    input_ = parser(read_input())
    # ONE #1
    part_1 = part1(input_)
    print(part_1)
    assert part_1 == 138
    # TWO #2
    part_2 = part2(input_)
    print(part_2)
    assert part_2 == 1771

